import React from "react";
import "./styles/component.css";
import { getActivityDef, getActivityModifierDef, getInventoryItemDef } from "@d2api/manifest-web";
import { DestinyActivityDefinition, DestinyActivityModifierDefinition, DestinyMilestoneActivityDefinition, DestinyInventoryItemDefinition } from "bungie-api-ts/destiny2";

import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';
import Row from 'react-bootstrap/Row';
import Tab from 'react-bootstrap/Tab';
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";

type weeklyNightfallResponse = {
	nightfallActivities: weeklyNightfallResponseInfo[] | undefined;
};

type weeklyNightfallResponseInfo = {
    activityHash: number,
    challengeObjectiveHashes: number[],
    modifierHashes: number[],
    booleanActivityOptions: {}
}

type nightfallLevelsInfoType = {
    activityInfo: DestinyActivityDefinition,
    modifiersInfo: (DestinyActivityModifierDefinition | undefined)[],
    rewardsInfo: (DestinyInventoryItemDefinition | undefined)[]
} | undefined;

type MyProps = {};

type MyState = {
	apiResponse: weeklyNightfallResponse;
};

class WeeklyNightfall extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				nightfallActivities: undefined
			},
		};
	}

	getWeeklyNightfallRewards() {
		fetch("http://localhost:5000/api/weekly_nightfall")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getWeeklyNightfallRewards();
	}

    getNightfallLevelsInfo() {
        if (this.state.apiResponse.nightfallActivities == undefined) return;
        const nightfallLevels = this.state.apiResponse.nightfallActivities.map(activity => {
			const activityInfo = getActivityDef(activity.activityHash);
            if (activityInfo == undefined) return;

			//get modifier info
			const modifiersInfo = activity.modifierHashes.map(modifier => {
				const modifierInfo = getActivityModifierDef(modifier);
				return modifierInfo;
			})
			const filteredModifierInfo = modifiersInfo.filter(mod => {
				return mod !== undefined && mod.displayProperties.name != "";
			});

			//get rewards info
			const rewardsInfo = activityInfo.rewards[0].rewardItems.filter(item => {
                return item !== undefined;
            }).map(reward => {
				const rewardInfo = getInventoryItemDef(reward.itemHash);
				return rewardInfo;
			});

			return {
				activityInfo,
				modifiersInfo: filteredModifierInfo,
				rewardsInfo
			};
		});
        return nightfallLevels;
    }

    renderModifiers(modifiersInfo: (DestinyActivityModifierDefinition | undefined)[]) {
        return modifiersInfo.filter(mod => {return mod !== undefined}).map(modifier => {
            if (modifier == undefined) return;
            let renderedDescription = modifier.displayProperties.description;
            if(modifier.displayProperties.name === "Champion Foes") {
                renderedDescription = this.renderBreakerIcons(renderedDescription);
            }
            if (modifier.displayProperties.name === "Shielded Foes") {
                renderedDescription = this.renderDamageIcons(renderedDescription);
            }
            return (
                <div key={modifier.hash}>
                    <OverlayTrigger
						key={modifier.hash}
						placement='top'
						overlay={
							<Tooltip id='modifier description'>
								<b>{modifier.displayProperties.name}</b>
                                <hr />
                                {renderedDescription}
							</Tooltip>
						}
					>
						<img
							src={`https://www.bungie.net${modifier.displayProperties.icon}`}
							className='modifierIcon'
							alt='strike modifier icon'
						/>
					</OverlayTrigger>
                </div>
            );
        })
    }

    renderNightfallLevelsCategories(nightfallLevels: nightfallLevelsInfoType[]) {
        return nightfallLevels.map(level => {
            if (level == undefined) return;
            return (
                <Nav.Item key={level.activityInfo.hash} className="pill">
                    <Nav.Link eventKey={level.activityInfo.displayProperties.name} className="pillLink">
                        {level.activityInfo.displayProperties.name}
                    </Nav.Link>
                </Nav.Item>
            );
        });
    }

    renderNightfallLevelsforCategory(nightfallLevels: nightfallLevelsInfoType[]) {
        console.log(nightfallLevels);
        return nightfallLevels.map(level => {
            if (level == undefined) return;
            return (
                <Tab.Pane eventKey={level.activityInfo.displayProperties.name} key={level.activityInfo.displayProperties.name}>
                    <div className="description">
                        <i>{level.activityInfo.selectionScreenDisplayProperties.description}</i>
                        <div>
                            Modifiers: 
                            <div className="display-in-row-wrap">
                                {this.renderModifiers(level.modifiersInfo)}
                            </div>
                        </div>
                        <div>
                            Rewards: 
                            <div>
                                {/* {this.renderRewards(rewardsInfo)} */}
                            </div>
                        </div>
                    </div>
                </Tab.Pane>
            );
        });
    }
    
    renderWeaponRotation() {
        return <div></div>;
    }

    renderRewards() {
        return <div></div>;
    }

	render() {
		if (this.state !== null && this.state.apiResponse.nightfallActivities !== undefined) {
            const nightfallLevels: nightfallLevelsInfoType[] | undefined = this.getNightfallLevelsInfo();
            if (nightfallLevels == undefined || nightfallLevels[0] == undefined) {
                return <div>error loading nightfall rotation</div>;
            }
			return (
				<div className="display-in-row">
                    <div className="rounded-corners ml5 mr5 rotationTable2 width60">
                        <div className="ml5 bg-teal pb5">
                            <h3>{nightfallLevels[0].activityInfo.displayProperties.description}</h3>
                        </div>
                        <div className="tabsTable">
                            <Tab.Container id="left-tabs-example" defaultActiveKey="Nightfall: Hero">
                                <Row>
                                    <Col sm={3}>
                                        <Nav variant="pills" className="flex-column">
                                            {this.renderNightfallLevelsCategories(nightfallLevels)}
                                        </Nav>
                                    </Col>
                                    <Col sm={9}>
                                        <Tab.Content>
                                            {this.renderNightfallLevelsforCategory(nightfallLevels)}
                                        </Tab.Content>
                                    </Col>
                                </Row>
                            </Tab.Container>
                        </div>
                    </div>
                    <div className="rotationTable1">
                        <h4>Nightfall Weapon Rotation</h4>
                        <hr />
                        <div>
                            {this.renderWeaponRotation()}
                        </div>
                    </div>
                </div>
			);
		}
	}
}

export default WeeklyNightfall;
