    activityHash: number,
    weaponHash: number
};

type MyProps = {};

type MyState = {
	apiResponse: wellspringResponse;
};

class Wellspring extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				bossName: "",
                activityHash: -1,
                weaponHash: -1
