import React, { FC } from "react";
import "./styles/component.css";
import "./styles/lostSector.css";
import { lostSectorType } from "../typeDefinitions/lostSectors";
import {
	getActivityDef,
	getActivityModifierDef,
	getInventoryItemDef,
	getBreakerTypeDef,
	getDamageTypeDef,
} from "@d2api/manifest-web";

import Card from "react-bootstrap/Card";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import {
	DestinyActivityModifierDefinition,
	DestinyInventoryItemDefinition,
} from "bungie-api-ts/destiny2";

type MyProps = {};

type MyState = {
	apiResponse: lostSectorType;
};

class LostSectorRotation extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				currLostSectorName: "",
				currLostSectorHashes: { master: -1, legend: -1 },
				currReward: "",
				lostSectorRotation: [],
			},
		};
	}

	getLostSectors() {
		fetch("http://localhost:5000/api/lost_sector")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getLostSectors();
	}

	renderDamageIcons(description: string) {
		const splitDesc = description.split(" ");

		const voidType = getDamageTypeDef(3454344768);
		const solarType = getDamageTypeDef(1847026933);
		const arcType = getDamageTypeDef(2303181850);
		const kineticType = getDamageTypeDef(3373582085);
		const stasisType = getDamageTypeDef(151347233);
		const strandType = getDamageTypeDef(3949783978);

		const renderedDesc = splitDesc.map(part => {
			switch (part) {
				case "[Solar]":
					return (
						<img
							key='solarIcon'
							className='smallIcon'
							src={`https://www.bungie.net${solarType?.displayProperties.icon}`}
							alt='solar element icon'
						/>
					);
				case "[Void]":
					return (
						<img
							key='voidIcon'
							className='smallIcon'
							src={`https://www.bungie.net${voidType?.displayProperties.icon}`}
							alt='void element icon'
						/>
					);
				case "[Arc]":
					return (
						<img
							key='arcIcon'
							className='smallIcon'
							src={`https://www.bungie.net${arcType?.displayProperties.icon}`}
							alt='arc element icon'
						/>
					);
				case "[Stasis]":
					return (
						<img
							key='stasisIcon'
							className='smallIcon'
							src={`https://www.bungie.net${stasisType?.displayProperties.icon}`}
							alt='stasis element icon'
						/>
					);
				case "[Strand]":
					return (
						<img
							key='strandIcon'
							className='smallIcon'
							src={`https://www.bungie.net${strandType?.displayProperties.icon}`}
							alt='strand element icon'
						/>
					);
				case "[Kinetic]":
					return (
						<img
							key='kineticIcon'
							className='smallIcon'
							src={`https://www.bungie.net${kineticType?.displayProperties.icon}`}
							alt='kinetic element icon'
						/>
					);
				default:
					return " " + part + " ";
			}
		});
		return renderedDesc;
	}

	renderBreakerIcons(description: string) {
		const splitDesc = description.split(" ");

		const shieldPiercing = getBreakerTypeDef(485622768);
		const disruption = getBreakerTypeDef(2611060930);
		const stagger = getBreakerTypeDef(3178805705);

		const renderedDesc = splitDesc.map(part => {
			switch (part) {
				case "[Shield-Piercing]":
					return (
						<img
							key='shieldPiercingIcon'
							className='smallIcon'
							src={`https://www.bungie.net${shieldPiercing?.displayProperties.icon}`}
							alt='anti-barrier icon'
						/>
					);
				case "[Disruption]":
					return (
						<img
							key='disruptionIcon'
							className='smallIcon'
							src={`https://www.bungie.net${disruption?.displayProperties.icon}`}
							alt='overload icon'
						/>
					);
				case "[Stagger]":
					return (
						<img
							key='staggerIcon'
							className='smallIcon'
							src={`https://www.bungie.net${stagger?.displayProperties.icon}`}
							alt='unstoppable icon'
						/>
					);
				default:
					return " " + part + " ";
			}
		});
		return renderedDesc;
	}

	renderVar(description: string) {
		const renderedDesc = description.replace(/{var:(?:\d{1,10})}/g, "25");
		return renderedDesc;
	}

	renderLostSectorRotation() {
		return this.state.apiResponse.lostSectorRotation.map(lostSectorName => {
			let classes = "display-in-row center-vertical";
			if (lostSectorName == this.state.apiResponse.currLostSectorName) {
				classes = classes.concat(" highlight");
			}
			return (
				<div key={lostSectorName}>
					<div className={classes}>{lostSectorName}</div>
				</div>
			);
		});
	}

	getRewards() {
		const masterInfo = getActivityDef(
			this.state.apiResponse.currLostSectorHashes.master
		);
		const legendInfo = getActivityDef(
			this.state.apiResponse.currLostSectorHashes.legend
		);

		//get master info
		let filteredMasterModifiers, masterRewards;
		if (masterInfo) {
			const masterModifiers = masterInfo.modifiers.map(
				({ activityModifierHash }) => {
					const modifierInfo =
						getActivityModifierDef(activityModifierHash);
					return modifierInfo;
				}
			);
			//filter hidden modifiers
			filteredMasterModifiers = masterModifiers.filter(mod => {
				if (mod == undefined) return;
				return (
					mod.displayInNavMode && mod.displayProperties.name !== ""
				);
			});

			//get reward info
			masterRewards = masterInfo.rewards.map(({ rewardItems }) => {
				const rewardInfo = getInventoryItemDef(rewardItems[0].itemHash);
				return rewardInfo;
			});
		} else {
			console.log("master lost sector not found");
		}

		//get legend info
		let legendRewards, filteredLegendModifiers;
		if (legendInfo) {
			const legendModifiers = legendInfo.modifiers.map(
				({ activityModifierHash }) => {
					const modifierInfo =
						getActivityModifierDef(activityModifierHash);
					return modifierInfo;
				}
			);

			if (legendModifiers) {
				filteredLegendModifiers = legendModifiers.filter(mod => {
					if (mod == undefined) return;
					return (
						mod.displayInNavMode &&
						mod.displayProperties.name !== ""
					);
				});

				//get reward info
				legendRewards = legendInfo.rewards.map(({ rewardItems }) => {
					const rewardInfo = getInventoryItemDef(
						rewardItems[0].itemHash
					);
					return rewardInfo;
				});
			}
		} else {
			console.log("legend lost sector not found");
		}
		return {
			masterInfo,
			legendInfo,
			legendRewards,
			legendModifiers: filteredLegendModifiers,
			masterRewards,
			masterModifiers: filteredMasterModifiers,
		};
	}

	renderModifiers(modifiers: DestinyActivityModifierDefinition[]) {
		if (modifiers == undefined) return;
		return modifiers.map(mod => {
			let renderedDescription;
			if (mod.displayProperties.description.includes("{")) {
				renderedDescription = this.renderVar(
					mod.displayProperties.description
				);
			} else {
				renderedDescription = mod.displayProperties.description;
			}

			if (mod.displayProperties.name === "Champion Foes") {
				renderedDescription =
					this.renderBreakerIcons(renderedDescription);
			} else if (
				mod.displayProperties.name.includes("Threat") ||
				mod.displayProperties.name.includes("Surge")
			) {
				renderedDescription =
					this.renderDamageIcons(renderedDescription);
			}
			return (
				<div key={mod.hash} className='icon-m5'>
					<OverlayTrigger
						key={mod.hash}
						placement='top'
						overlay={
							<Tooltip id='modifier description'>
								<b>{mod.displayProperties.name}</b>
								<hr />
								{renderedDescription}
							</Tooltip>
						}
					>
						<img
							src={`https://www.bungie.net${mod.displayProperties.icon}`}
							className='rewardIcon'
							alt='mod icon'
						/>
					</OverlayTrigger>
				</div>
			);
		});
	}

	renderRewards(
		rewards: DestinyInventoryItemDefinition[],
		currReward: string
	) {
		if (rewards == undefined) return;
		return rewards.map(reward => {
			if (
				reward.displayProperties.name.includes("Exotic") &&
				!reward.displayProperties.name.includes(currReward)
			) {
				return "";
			}
			return (
				<div key={reward.hash}>
					<img
						src={`https://www.bungie.net${reward.displayProperties.icon}`}
						className='rewardIcon'
						alt='reward icon'
					/>
					{reward.displayProperties.name}
				</div>
			);
		});
	}

	renderLostSectors() {
		const lostSectorsInfo = this.getRewards();
		const lostSectorName = this.state.apiResponse.currLostSectorName;
		const currReward = this.state.apiResponse.currReward;
		return (
			<div className='display-in-row'>
				<Card style={{ width: "25rem" }}>
					<Card.Img
						variant='top'
						src={`https://www.bungie.net${lostSectorsInfo.legendInfo?.pgcrImage}`}
					/>
					<Card.Body>
						<Card.Title>{lostSectorName} - Legend</Card.Title>
						<div className='display-in-row-wrap'>
							{this.renderModifiers(
								lostSectorsInfo.legendModifiers as DestinyActivityModifierDefinition[]
							)}
						</div>
						<hr />
						<div>
							<b>Rewards:</b>
							{this.renderRewards(
								lostSectorsInfo.legendRewards as DestinyInventoryItemDefinition[],
								currReward
							)}
						</div>
					</Card.Body>
				</Card>
				<Card style={{ width: "25rem" }}>
					<Card.Img
						variant='top'
						src={`https://www.bungie.net${lostSectorsInfo.masterInfo?.pgcrImage}`}
					/>
					<Card.Body>
						<Card.Title>{lostSectorName} - Master</Card.Title>
						<div className='display-in-row-wrap'>
							{this.renderModifiers(
								lostSectorsInfo.masterModifiers as DestinyActivityModifierDefinition[]
							)}
						</div>
						<hr />
						<div>
							<b>Rewards:</b>
							{this.renderRewards(
								lostSectorsInfo.masterRewards as DestinyInventoryItemDefinition[],
								currReward
							)}
						</div>
					</Card.Body>
				</Card>
			</div>
		);
	}

	render() {
		if (this.state !== null) {
			return (
				<div>
					{this.renderLostSectors()}
					<div className='nightfallRow1'>
						<h4>Nightfall Weapon Rotation</h4>
						<hr />
						<div>{this.renderLostSectorRotation()}</div>
					</div>
				</div>
			);
		}
	}
}

export default LostSectorRotation;
