import React from "react";
import "./styles/component.css";
import { getInventoryItemDef } from "@d2api/manifest-web";

type wellspringResponse = {
	currRotationIndex: number;
};

type MyProps = {};

type MyState = {
	apiResponse: wellspringResponse;
};

class Wellspring extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				currRotationIndex: -1
			},
		};
	}

	getWellspringRotation() {
		fetch("http://localhost:5000/api/wellspring")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getWellspringRotation();
	}

	render() {
		if (this.state !== null) {
			return (
				<div className='bg-teal itemCard'>
					<div className='display-in-row center-vertical'>
						{/* <img
							src={`https://www.bungie.net${itemImageSrc}`}
							className='p5'
							alt='altars of sorrow reward'
						/> */}
						{this.state.apiResponse.currRotationIndex}
					</div>
				</div>
			);
		}
	}
}

export default Wellspring;
