import React from "react";
import "./styles/component.css";
import { getInventoryItemDef } from "@d2api/manifest-web";
import { activityResponse } from "../typeDefinitions/activityTypes";

type MyProps = {
    activityType: string
};

type MyState = {
	apiResponse: activityResponse;
};

class ActivityCard extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {},
		};
	}

	getAltarOfSorrowRewards() {
		fetch("http://localhost:5000/api/altarsOfSorrow")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getAltarOfSorrowRewards();
	}

    renderActivities() {

    }

	render() {
        let activityToRender;
		if (this.props.activityType == 'cruciblePlaylist' && this.props.cruciblePlaylist) {
            activityToRender = this.props.cruciblePlaylist;
        } else if (this.props.activityType == 'nightmareHunts' && this.props.nightmareHunts) {
            activityToRender = this.props.nightmareHunts;
        }
        return (
            <div className="display-in-row">
                {this.renderActivities(activityToRender)}
            </div>
        );
	}
}

export default ActivityCard;
