import React from "react";
import "./styles/component.css";
import { nightmareHuntResponse } from "../typeDefinitions/activityTypes";
import { getActivityDef } from "@d2api/manifest-web";

import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";

type MyProps = {
    activityType: string
};

type MyState = {
	nightmareHuntResponse: nightmareHuntResponse;
};

class ActivityCard extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			nightmareHuntResponse: {hunts: []},
		};
	}

	getNightmareHunts() {
		fetch("http://localhost:5000/api/nightmare_hunts")
			.then(res => res.json())
			.then(res => this.setState({ nightmareHuntResponse: res }));
	}

    getCruciblePlaylists() {
        fetch("http://localhost:5000/api/crucible_playlist")
			.then(res => res.json())
			.then(res => this.setState({ nightmareHuntResponse: res }));
    }

	componentDidMount() {
        if (this.props.activityType == "nightmareHunts") {
            this.getNightmareHunts();
        }
	}

    getNightmareHuntsInfo() {
        if (this.state.nightmareHuntResponse == undefined || this.state.nightmareHuntResponse.hunts == undefined) return;
        const currHuntsInfo = this.state.nightmareHuntResponse.hunts.map(hunt => {
			const huntInfo = getActivityDef(hunt);
			return huntInfo;
		});
        console.log(currHuntsInfo);
        return currHuntsInfo;
    }

    getCruciblePlaylistInfo() {
        const activitiesToExclude = [
			2259621230, 2754695317, 2607135461, 2696116787,
		];

		// const cruciblePlaylistInfo = cruciblePlaylistMilestoneInfo.activities
		// 	.map(activity => {
		// 		const activityInfo = manifest.getActivityInfo(
		// 			activity.activityHash
		// 		);
		// 		// console.log(activity.activityHash, activityInfo.displayProperties.name);
		// 		return activityInfo;
		// 	})
		// 	.filter(activity => {
		// 		return !activitiesToExclude.includes(activity.hash);
		// 	});
    }

    renderActivities(activityInfo: string) {
        if (activityInfo == "nightmareHunts" && this.state.nightmareHuntResponse !== null && this.state.nightmareHuntResponse.hunts !== null) {
            const huntsInfo = this.getNightmareHuntsInfo();
            if (huntsInfo == undefined) return <div>error loading nightmare hunts</div>
            return huntsInfo.map(activity => {
                if (activity == undefined) return <div>error loading hunt</div>;
                return (
                    <div key={activity.hash} className="bg-teal itemCard min-width rounded-corners mr5">
                        <OverlayTrigger
                            key={activity.hash}
                            placement="top"
                            overlay={
                                <Tooltip id='activity description'>
                                    <b>{activity.displayProperties.description}</b>
                                </Tooltip>
                            }
                        >
                            <div>
                                {activity.displayProperties.name}
                            </div>
                        </OverlayTrigger>
                    </div>
                );
            });
        } else if (activityInfo == "cruciblePlaylist") {
            return <div>Crucible</div>;
        }
        return <div>Activity type not found</div>;
    }

	render() {
        return (
            <div className="display-in-row">
                {this.renderActivities(this.props.activityType)}
            </div>
        );
	}
}

export default ActivityCard;
