import React, { FC } from "react";
import './styles/component.css';
// import './styles/lostSector.css';
import { getInventoryItemDef } from "@d2api/manifest-web";

import Card from 'react-bootstrap/Card';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import { DestinyActivityModifierDefinition, DestinyInventoryItemDefinition } from "bungie-api-ts/destiny2";

type altarOfSorrowsResponse = {
    currReward: string,
	currRewardInfo: string
}

type MyProps = {};

type MyState = {
	apiResponse: altarOfSorrowsResponse;
};

class AltarsOfSorrow extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = { apiResponse: {
            currReward: "",
            currRewardInfo: "" } 
        };
	}

	getAltarOfSorrowRewards() {
		fetch("http://localhost:5000/api/altarsOfSorrow")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getAltarOfSorrowRewards();
	}

    getItemImage(itemHash: number){
        const itemInfo = getInventoryItemDef(itemHash);
    }

	render() {
		if (this.state !== null) {
            const itemImageSrc = this.getItemImage(this.state.apiResponse.currRewardInfo);
			return (
                <div className="bg-teal itemCard">
                    <div className="display-in-row center-vertical">
                        {/* <img src={`https://www.bungie.net${reward.displayProperties.icon}`} className="iconImage" alt="altars of sorrow reward"/> */}
                        {this.state.apiResponse.currReward}
                    </div>
                </div>
            );
		}
	}
}

export default AltarsOfSorrow;
