import React from "react";
import "./styles/component.css";
import { getInventoryItemDef, getActivityDef } from "@d2api/manifest-web";
import { DestinyActivityDefinition, DestinyInventoryItemDefinition } from "bungie-api-ts/destiny2";

import Card from 'react-bootstrap/Card';

type empireHuntResponse = {
	bossName: string,
    activityHash: number,
    weaponHash: number
};

type MyProps = {};

type MyState = {
	apiResponse: empireHuntResponse;
};

class EmpireHunt extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				bossName: "",
                activityHash: -1,
                weaponHash: -1
			},
		};
	}

	getEmpireHuntRotation() {
		fetch("http://localhost:5000/api/empire_hunt")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getEmpireHuntRotation();
	}

