                <div key={activity.hash}>
                    <Card style={{ width: '25rem' }}>
                        <Card.Img variant="top" src={`https://www.bungie.net${activity.pgcrImage}`} />
                        <Card.Body>
                            <Card.Title>{activity.displayProperties.name}</Card.Title>
                        </Card.Body>
                    </Card>
                </div>
                
			);
		}
	}
}

export default EmpireHunt;
