import React from "react";
import "./styles/component.css";
import { getInventoryItemDef, getActivityDef } from "@d2api/manifest-web";
import { DestinyActivityDefinition, DestinyInventoryItemDefinition } from "bungie-api-ts/destiny2";

type wellspringResponse = {
	bossName: string,
    activityHash: number,
    weaponHash: number
};

type wellspringInfoType = {
    activityInfo: DestinyActivityDefinition,
    rewardInfo: DestinyInventoryItemDefinition,
    activityRewards: DestinyInventoryItemDefinition[]
};

type MyProps = {};

type MyState = {
	apiResponse: wellspringResponse;
};

class Wellspring extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				bossName: "",
                activityHash: -1,
                weaponHash: -1
			},
		};
	}

	getWellspringRotation() {
		fetch("http://localhost:5000/api/wellspring")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getWellspringRotation();
	}

    getInfo(wellspringInfo: wellspringResponse) {
		const activityInfo = getActivityDef(wellspringInfo.activityHash);
		const rewardInfo = getInventoryItemDef(wellspringInfo.weaponHash);

        if (activityInfo == undefined) return;
		const activityRewards = activityInfo.rewards.map(({rewardItems}) => {
			const rewards = rewardItems.map(reward => {
				const rewardData = getInventoryItemDef(reward.itemHash);
				return rewardData;
			});
			return rewards;
		});
        return {
            activityInfo,
            rewardInfo,
            activityRewards
        };
    }

    renderRewards(wellspringInfo: wellspringInfoType) {

    }

	render() {
		if (this.state !== null) {
            const wellspringInfo = this.getInfo(this.state.apiResponse);
			return (
				<div className='bg-teal itemCard'>
					<div>
                        <h3>
                            {wellspringInfo?.activityInfo.displayProperties.name}
                        </h3>
                        <div>
                            {wellspringInfo?.activityInfo.displayProperties.description}
                        </div>
                    </div>
                    <hr />
                    {this.renderRewards(wellspringInfo || {})}
				</div>
			);
		}
	}
}

export default Wellspring;
