import {
  getLatestCachedVersion, includeTables, loadDefs, setApiKey, verbose
} from "@d2api/manifest-web";
import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import './App.css';

import Daily from './components/Daily';
import Dashboard from './components/Dashboard';
import { DefsProvider } from "./components/DefsProvider";
import Header from './components/Header';
import Nightfall from './components/Nightfall';
import RaidDungeon from "./components/RaidDungeon";
import Weekly from './components/Weekly';

const keys = require("./config/keys.json");
const neededManifestDefs = require("./data/neededManifestDefs.json");

function App() {
  verbose();
  setApiKey(keys.apiKey);
  includeTables(neededManifestDefs.tablesNeeded);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const manifestLoader = async () => {
      // checks the API for the current version.
      // loads our cached copy if it's up to date, or downloads a new one from bungie
      await loadDefs();
      await getLatestCachedVersion("en");
    }

    manifestLoader().catch(console.error).then(() => {setIsLoaded(true)});
  }, []);

  function renderRoutes() {
    if (!isLoaded) {
      return (
        <div className="info">
          loading manifest...
        </div>

      );
    } else {
      return (
        <Routes>
          <Route path="/" element={<Dashboard/>} />
          <Route path="/weekly" element={<Weekly />} />
          <Route path="/daily" element={<Daily />}/>
          <Route path="/nightfall" element={<Nightfall />}/>
          <Route path="/raiddungeon" element={<RaidDungeon />}/>
          {/* <Route path="/vendors" element={<Vendors />}/> */}
          {/* <Route path="/other" element={<Other />}/> */}
          {/* <Route path="/calendar" element={<Calendar />}/> */}
        </Routes>
      );
    }
  }

  return (
    <div className="App">
      <DefsProvider>
        <BrowserRouter>
          <Header />
          {renderRoutes()}
        </BrowserRouter>
      </DefsProvider>
    </div>
  );
}

export default App;
