import React from "react";
import "./styles/component.css";
import { getInventoryItemDef, getActivityDef } from "@d2api/manifest-web";
import { DestinyActivityDefinition, DestinyInventoryItemDefinition } from "bungie-api-ts/destiny2";

type empireHuntResponse = {
	bossName: string,
    activityHash: number,
    weaponHash: number
};

type wellspringInfoType = {
    activityInfo: DestinyActivityDefinition,
    rewardInfo: DestinyInventoryItemDefinition,
    activityRewards: DestinyInventoryItemDefinition[]
};

type MyProps = {};

type MyState = {
	apiResponse: empireHuntResponse;
};

class EmpireHunt extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			apiResponse: {
				bossName: "",
                activityHash: -1,
                weaponHash: -1
			},
		};
	}

	getEmpireHuntRotation() {
		fetch("http://localhost:5000/api/empire_hunt")
			.then(res => res.json())
			.then(res => this.setState({ apiResponse: res }));
	}

	componentDidMount() {
		this.getEmpireHuntRotation();
	}

    getInfo(empireHuntInfo: empireHuntResponse) {
		
    }

    renderRewards(wellspringInfo: wellspringInfoType) {
        return (
            <div className="display-in-row" key={wellspringInfo.rewardInfo.hash}>
                <img src={`https://www.bungie.net${wellspringInfo.rewardInfo.displayProperties.icon}`} className="md-rewardIcon" alt="wellspring reward icon"/>
                <div>{wellspringInfo.rewardInfo.displayProperties.name}</div>
            </div>
        );
    }

	render() {
		if (this.state !== null && this.state.apiResponse.bossName !== "") {
            const wellspringInfo: wellspringInfoType | undefined = this.getInfo(this.state.apiResponse);
            if (wellspringInfo == undefined) return (
                <div>error loading Wellspring reward</div>
            );
			return (
				<div className='bg-teal itemCard'>
					<div>
                        <h3>
                            {wellspringInfo.activityInfo.displayProperties.name}
                        </h3>
                        <div>
                            {wellspringInfo.activityInfo.displayProperties.description}
                        </div>
                    </div>
                    <hr />
                    {this.renderRewards(wellspringInfo)}
				</div>
			);
		}
	}
}

export default EmpireHunt;
