import React from "react";
import "./styles/component.css";
import { nightmareHuntResponse } from "../typeDefinitions/activityTypes";
import { getActivityDef } from "@d2api/manifest-web";

type MyProps = {
    activityType: string
};

type MyState = {
	nightmareHuntResponse: nightmareHuntResponse;
};

class ActivityCard extends React.Component<MyProps, MyState> {
	constructor(props: MyProps) {
		super(props);
		this.state = {
			nightmareHuntResponse: {hunts: []},
		};
	}

	getNightmareHuntRewards() {
		fetch("http://localhost:5000/api/nightmare_hunts")
			.then(res => res.json())
			.then(res => this.setState({ nightmareHuntResponse: res }));
	}

	componentDidMount() {
        if (this.props.activityType == "nightmareHunts") {
            this.getNightmareHuntRewards();
        }
	}

    getNightmareHuntsInfo() {
        const currHuntsInfo = this.state.nightmareHuntResponse.hunts.map(hunt => {
			const huntInfo = getActivityDef(hunt);
			return huntInfo;
		});
        return currHuntsInfo;
    }

    renderActivities(activityType: string) {
        if (activityType == "nightmareHunts" && this.state.nightmareHuntResponse !== null) {
            const huntsInfo = getNightmareHuntsInfo();
            return <div>Nightmare Hunt</div>;
        } 
        return <div>Activity type not found</div>;
    }

	render() {
        return (
            <div className="display-in-row">
                {this.renderActivities(this.props.activityType)}
            </div>
        );
	}
}

export default ActivityCard;
